import axios from "axios";
import React, {useState, useEffect} from "react";
import DisplayWeather from "./DisplayWeather";


const Weather = () => {


    const APIKEY = "1116f4c4e6007e63fa30e4b4bfbd5f43";

    const [weather, setWeather] = useState([]);


    const [ form, setForm ] = useState({
        city: "",
        country: ""
    })

    async function weatherData(e){
        e.preventDefault();
        if(form.city == ""){
            alert("Add values");
        }else{
            const data = await fetch(
                `https://api.openweathermap.org/data/2.5/weather?q=${form.city},${form.country}&appid=${APIKEY}`
                ).then(
                    (res) => res.json()
                ).then((data) => data);

                setWeatherData(
                    {data: data.data}
                );

        }
    }


    const handleChange = (e) =>{
        let name = e.target.name;
        let value = e.target.value;

        if(name == "city"){
            setForm({...form, city:value});

        }
        if(name == "country"){
            setForm({...form, country:value});

        }

        console.log(form.city, form.country);
    }


    return (  
        <div>
            <h3>Väder</h3>

            <div className="search">
                <form>
                    <input type="text" name="city" id="city" onChange={e => handleChange(e)}/>
                    <input type="text" name="country" id="country" onChange={e => handleChange(e)} />
                    <button onClick={(e) => weatherData(e)}>Sök</button>
                </form>
            </div>


            {
                weaterData.data != undefined ?
                    <div>
                        <DisplayWeather data={weaterData}/>
                    </div>
                :null
            }
        </div>

    );
}

export default Weather;
