import axios from "axios";
import React, { useState } from "react";
import DisplayWeather from "./DisplayWeather";
import ForecastData from "./Forecast";

const Weather = () => {
  const APIKEY = "1116f4c4e6007e63fa30e4b4bfbd5f43";

  const [weather, setWeather] = useState({});
  const [forecast, setForecast] = useState({});
  const [lat, setLat] = useState("");
  const [lon, setLon] = useState("");
  const [form, setForm] = useState({
    city: "",
    country: ""
  });



  function handleClick(e){
    weatherData(e)
    forecastWeather()
  }
  async function weatherData(e) {
    e.preventDefault();
    if (form.city === "") {
      alert("Add values");
    } else {
      try {
        const response = await axios.get(
          `https://api.openweathermap.org/data/2.5/weather?q=${form.city},${form.country}&appid=${APIKEY}&units=metric`
        );
        const data = response.data;
        setWeather(data);
        setLat (data.coord.lat);
        setLon(data.coord.lon);



      } catch (error) {
        console.log(error);
      }
    }

  }



  async function forecastWeather() {
    const exclude = "current,minutely,hourly"; // Exclude the current, minutely, and hourly forecasts
    const cnt = 8; // Number of forecast days

    if (form.city === "") {
      alert("Add values");
    } else {
      try {
        const response = await axios.get(
          `https://api.openweathermap.org/data/3.0/onecall?lat=${lat}&lon=${lon}&exclude=${exclude}&appid=${APIKEY}&units=metric&cnt=${cnt}`
        );

        const forecastData = response.data.daily;
        setForecast(forecastData);
      } catch (error) {
        console.log(error);
      }
    }
  }



  const handleChange = (e) => {
    let name = e.target.name;
    let value = e.target.value;

    if (name === "city") {
      setForm({ ...form, city: value });
    }
    if (name === "country") {
      setForm({ ...form, country: value });
    }


  }

  return (
    <div>
      <div className="search-bar">
        <h1>VäderAppen</h1>
        <form>
          <input type="text" name="city" id="city" onChange={e => handleChange(e)} />
          <input type="text" name="country" id="country" onChange={e => handleChange(e)} />
          <br />
          <button onClick={(e) => handleClick(e)}>Sök</button>
        </form>
      </div>

      {weather !== undefined ? (
        <div>
          <DisplayWeather data={weather} />
          <ForecastData data={forecast}/>
        </div>
      ) : null}
    </div>
  );
}

export default Weather;
